
public class HW3_DoubleArrayCountries {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		String countries [][]=new String [2][3];
		
		//first row
		countries[0] [0]="Germany";
		countries[0] [1]="Canada";
		countries[0] [2]="China";
		
		//second row
		countries[1] [0]="Australia";
		countries[1] [1]="Japan";
		countries[1] [2]="Mexico";
		
	    System.out.println(countries[0][0]);
	    System.out.println(countries[0][1]);
	    System.out.println(countries[0][2]);
	    System.out.println(countries[1][0]);
	    System.out.println(countries[1][1]);
	    System.out.println(countries[1][2]);

		
	}

}
