
public class If_Else {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		int i=300;
		int j=200;
		int a=300;
		
		
		if (i==j) //condition
		System.out.println("value of i & j are equal");
		
		else
			System.out.println("Value of i & j are not equal");
		
		
		if (j<i) //condition
			System.out.println("value of j is less than i");
		
		else
			System.out.println("Value of j is not less than i");
		
		if (!(i==j))  //condition
			System.out.println("Value of i & j are not equal");
		
		else
			System.out.println("Value of i & j are equal");
		
		
		if (i==a) //condition
			System.out.println("i & a are equal");
		
		if (i>j) //condition
			System.out.println("Value of i is greater than j");
		
		
		
		
		
		
		
		
	}

}
