
public class Inheritance extends non_static {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		//Inheritance helps us to take functions or variable from a different class
		
	    country();
	
		Inheritance x=new Inheritance();
		x.car();
		x.flower();
		x.school();
		x.bike();
		
		
	}

	public static void country(){
		
	System.out.println("I am from Mali"); //overriding the country function.
	
	
	
	}
}
